digraph {
    rankdir=TB;
    node [fontname="sans-serif", shape=box, style=rounded];
    edge [fontname="sans-serif"];

    // Start Node
    Start [label="外付けモニターが映らない", shape=oval, style=filled, fillcolor=lightblue];

    // Step 1: Basic Checks
    PowerCheck [label="STEP 1: モニター本体の電源を確認\n(ケーブル接続、電源ランプ)"];
    SignalCheck [label="モニターに「信号なし」等の\nメッセージは表示されるか？", shape=diamond];

    // Step 2: Connection Check
    CableCheck [label="STEP 2: 映像ケーブルの確認\n(両端をしっかり挿し直す)"];
    
    // Step 3: PC Output Settings
    PCOutput [label="STEP 3: PCの出力設定を確認\n(Win + Pキーで「複製」か「拡張」を選択)"];

    // Step 4: Swap Testing
    SwapTest [label="STEP 4: 最終切り分け\n(別のケーブルやモニターで試す)"];
    SwapResult [label="別の機材で映ったか？", shape=diamond];

    // End Nodes (Outcomes)
    Result_PCBoot [label="PC本体の起動を確認してください", shape=box, style=filled, fillcolor=lightcoral];
    Result_CableOrMonitor [label="原因：ケーブルまたはモニターの故障", shape=box, style=filled, fillcolor=orange];
    Result_PCFault [label="原因：PC本体の故障の可能性大\n(専門部署へエスカレーション)", shape=box, style=filled, fillcolor=lightcoral];
    End_Success [label="解決", shape=oval, style=filled, fillcolor=lightgreen];

    // Flow Logic
    Start -> PowerCheck;
    PowerCheck -> SignalCheck;
    
    SignalCheck -> CableCheck [label="はい（メッセージ表示）"];
    SignalCheck -> Result_PCBoot [label="いいえ（完全な真っ暗）"];
    
    CableCheck -> PCOutput;
    PCOutput -> SwapTest;
    
    SwapTest -> SwapResult;
    SwapResult -> Result_CableOrMonitor [label="はい"];
    SwapResult -> Result_PCFault [label="いいえ"];
    
    Result_CableOrMonitor -> End_Success;
}

